cy.scrollTo("bottom");
cy.wait(500);
cy.get(".fixed.bottom-3.right-3").should("be.visible");
// Clique dessus
cy.get(".fixed.bottom-3.right-3 button").click();
cy.wait(1000);
// Vérifie que la page est revenue en haut
cy.window().its("scrollY").should("eq", 0);
